import { Component } from '@angular/core';

@Component({
    selector: 'app-lotte-animate',
    template: ` <lottie-animation-view
            [options]="lottieConfig"
            [width]="300"
            [height]="600"
            (animCreated)="handleAnimation($event)"
        >
        </lottie-animation-view>
        <div id="player">
            <p>Speed: x{{ animationSpeed }}</p>
            <div class="range-container">
                <input
                    #range
                    type="range"
                    value="1"
                    min="0"
                    max="3"
                    step="0.5"
                    (change)="setSpeed(range.value)"
                />
            </div>
            <button (click)="stop()">stop</button>
            <button (click)="pause()">pause</button>
            <button (click)="play()">play</button>
        </div>`,
    // templateUrl: './lotte-animate.component.html',
    // styleUrls: ['./lotte-animate.component.scss'],
})
export class LotteAnimateComponent {
    public lottieConfig: Object;
    private anim: any;
    public animationSpeed: number = 1;

    constructor() {
        this.lottieConfig = {
            path: '~/assets/pinjump.json',
            autoplay: true,
            loop: true,
        };
    }

    handleAnimation(anim: any) {
        this.anim = anim;
    }

    stop() {
        this.anim.stop();
    }

    play() {
        this.anim.play();
    }

    pause() {
        this.anim.pause();
    }

    setSpeed(speed: any) {
        this.animationSpeed = speed * 1;
        this.anim.setSpeed(speed * 1);
    }
}
