import {Component, OnInit} from '@angular/core';
import {AnimationOptions} from "ngx-lottie";
import {AnimationItem} from "lottie-web";

@Component({
  selector: 'app-splash-screen',
  templateUrl: './splash-screen.component.html',
  styleUrls: ['./splash-screen.component.scss']
})
export class SplashScreenComponent implements OnInit {

  // This is the option that uses the package's AnimationOption interface
  options: AnimationOptions = {
    path: '../../assets/data.json'
  };

  constructor() { }

  ngOnInit(): void {  }

  // This is the component function that binds to the animationCreated event from the package
  onAnimate(animationItem: AnimationItem): void {
    console.log(animationItem);
  }
}
<div class="lottie-animation mt-10">
  <ng-lottie [options]="options" (animationCreated)="onAnimate($event)">
  </ng-lottie>
</div>

<div class="container-sm" style="text-align: center">
  <div class="splash-text">
    <h1>Step 1</h1>

    Lorem ipsum dolor sit amet consectetur adipisicing elit. Impedit eligendi
    obcaecati quisquam molestias animi dolores vitae veniam ut provident
    repudiandae doloremque quae quo vel, magnam labore eveniet natus fugiat.
    Aspernatur.
  </div>

  <div class="button-div mt-3 mb-10">
    <a class="btn btn-md accent light" style="margin: 1rem"> Previous </a>

    <a class="btn btn-md primary" style="margin: 1rem"> Next </a>
  </div>
</div>
